import React from 'react'
import styled from 'styled-components'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

// components
import Layout from '../components/Layout'
import {dummyBeers} from '../components/dummyBeer';
import Card from '../components/card';
import { Button } from "../componenet/Header";

// icon 
import { faSearch } from "@fortawesome/free-solid-svg-icons";

const BlockContainer = styled.div`
  height: 75px;
`
const FlexContainer = styled.div`
  display: flex;
  flex-flow: ${props => props.direction} wrap;
  justify-content: space-around;
  width: 100%;
  margin: 0 auto;
`
// This is our initialised `NextI18Next` instance
import { withNamespaces, i18n } from '../i18n'

class IndexPage extends React.Component {
  static async getInitialProps({ req }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return {
      userAgent,
      namespacesRequired: ['common']
    }
  }

  constructor(props){
    super(props);
    this.state ={
      stateBeer:dummyBeers
    }
  }  

  componentDidMount() {
    console.log(i18n.language)
  }

  render() {
    const list = [
      { href:"/", flex:"1" },
      { href:"/", flex:"2" },
      { href:"/", flex:"2" },
      { href:"/", flex:"1" },
      { href:"/", flex:"1" },
      { href:"/", flex:"1" },
      { href:"/", flex:"1" }
    ]
    return (
      <Layout t={this.props.t}>
        <BlockContainer />
        <FlexContainer direction="row">
          <BeerFilter>
            <h2>Filter by:</h2>
            <ul>
              <li>
                페일에일
                <input type="checkbox" />
              </li>
              <li>
                스카우터
                <input type="checkbox" />
              </li>
              <li>
                포터
                <input type="checkbox" />
              </li>
              <li>
                라거
                <input type="checkbox" />
              </li>
              <li>
                밀<input type="checkbox" />
              </li>
              <li>
                과일
                <input type="checkbox" />
              </li>
              <li>
                트라피스트
                <input type="checkbox" />
              </li>
              <li>
                기타
                <input type="checkbox" />
              </li>
            </ul>
          </BeerFilter>
          <BeerContent>
            <span>
              <span>
                <FontAwesomeIcon icon={faSearch} />
              </span>
              <input placeholder="Search Beer and more..." />
              <Button black>Sort by: Newest</Button>
            </span>
            <CardBox>
              {this.state.stateBeer.map((localState) => (
                <Card key={localState.id} content={localState} />
              ))}
            </CardBox>
          </BeerContent>
        </FlexContainer>
      </Layout>
    )
  }
}

const CardBox = styled.div`
  display: flex;
  padding: 40px 0;
  flex-wrap: wrap;
`;

const BeerFilter = styled.div`
  padding: 0 40px;
  width: 200px;
  > ul {
    margin-top: 60px;
    list-style: none;
    padding: 0;
    > li {
      padding: 12px 0;
      font-size: 13px;
      position: relative;
      > input {
        position: absolute;
        right: 0;
        margin: 0;
      }
    }
  }
`;

const BeerContent = styled.div`
  padding: 0 40px;
  flex: 1;
  span {
    position: relative;
    > span {
      position: absolute;
      top: 0;
      left: 24px;
      > svg {
        fill: #333;
      }
    }
    > input {
      border-radius: 20px;
      background: #f8f8f8;
      border: 0;
      padding: 20px 20px 20px 56px;
      max-width: 1000px;
      width: 100%;
      margin-top: 7px;
      &::placeholder {
        color: #b2b2b2;
      }
    }
    > button {
      float: right;
    }
  }
`;

export default withNamespaces('common')(IndexPage)
